mapper.select:
SELECT

FROM user
WHERE user_name = #{username} AND password = #{password}

mapper.update:
UPDATE user SET user_id = #{userId}, user_account = #{userAccount}, user_name = #{userName},
password = #{password},
nick_name = #{nickName}, user_gender = #{userGender}, email = #{email}, phone = #{phone}, status = #{status}, user_roles = #{userRoles, typeHandler=com.github.kevin.oasis.config.JsonTypeHandler}, update_by = #{updateBy}, update_time = NOW() WHERE id = #{id}

mapper.select:
SELECT

FROM user


        AND user_id = #{request.userId}


        AND user_account LIKE CONCAT('%', #{request.userAccount}, '%')


        AND user_name LIKE CONCAT('%', #{request.userName}, '%')


        AND user_gender = #{request.userGender}


        AND nick_name LIKE CONCAT('%', #{request.nickName}, '%')


        AND phone LIKE CONCAT('%', #{request.userPhone}, '%')


        AND email LIKE CONCAT('%', #{request.userEmail}, '%')


        AND status = #{request.status}


ORDER BY create_time DESC

    LIMIT #{request.size} OFFSET #{request.current}

SELECT COUNT(*)
FROM user


        AND user_id = #{request.userId}


        AND user_account LIKE CONCAT('%', #{request.userAccount}, '%')


        AND user_name LIKE CONCAT('%', #{request.userName}, '%')


        AND user_gender = #{request.userGender}


        AND nick_name LIKE CONCAT('%', #{request.nickName}, '%')


        AND phone LIKE CONCAT('%', #{request.userPhone}, '%')


        AND email LIKE CONCAT('%', #{request.userEmail}, '%')


        AND status = #{request.status}

mapper.delete:
DELETE FROM user
WHERE id IN

    #{id}

mapper.update:
UPDATE user
SET password = #{newPassword},
    update_time = NOW()
WHERE user_account = #{userAccount}
  AND password = #{oldPassword}
